import GlobalLayout from '../components/GlobalLayout.js'
import { withRouter } from 'next/router'
import CustomPage from '../components/CustomPage.js'
import fetch from 'isomorphic-unfetch'

const Index = (props) => (
    <GlobalLayout>
        <CustomPage name={props.router.query.page}/>
        <h4>{props.data.length}</h4>
    </GlobalLayout>
)

Index.getInitialProps = async function() {
    const res = await fetch('https://api.tvmaze.com/search/shows?q=batman');

    console.log(`Show data fetched. Count: ${res.json().length}`)

    return {
        data: res
    }
}

export default withRouter(Index)
